package switchcase;

public class switchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int day=3;
		
		switch (day) {
		
		case 1:
			System.out.println("sanku");
			break;
			
		
		case 2:
			System.out.println("tanku");
			break;
			
		case 3:
			System.out.println("sankuii");
			break;
		} 

	}

}
